window.addEventListener('load',function(){var e=document.getElementById('pageLoader');if(e){setTimeout(function(){e.classList.add('hidden')},1500)}});window.addEventListener('scroll',function(){var e=document.getElementById('progressBar');if(e){var t=window.pageYOffset,n=document.body.scrollHeight-window.innerHeight,o=n>0?t/n*100:0;e.style.width=o+'%'}});const observer=new IntersectionObserver(function(e){e.forEach(t=>{t.isIntersecting&&t.target.classList.add('visible')})},{threshold:.1});document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('.fade-in').forEach(e=>{observer.observe(e)})});document.addEventListener('DOMContentLoaded',function(){const e=document.getElementById('contactOverlay'),t=document.getElementById('contactClose');document.querySelectorAll('[data-contact-open]').forEach(n=>{n.addEventListener('click',function(n){n.preventDefault(),e&&e.classList.add('active')})}),t&&e&&t.addEventListener('click',function(){e.classList.remove('active')}),e&&(e.addEventListener('click',function(t){t.target===e&&e.classList.remove('active')}),window.addEventListener('keydown',t=>{'Escape'===t.key&&e.classList.remove('active')}))});document.addEventListener('DOMContentLoaded',function(){document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener('click',function(e){e.preventDefault();const t=this.getAttribute('href').substring(1),n=document.getElementById(t);n&&n.scrollIntoView({behavior:'smooth'})})})});window.addEventListener('scroll',function(){const e=document.querySelectorAll('.section'),t=document.querySelectorAll('.nav-link');let n='';e.forEach(e=>{e.offsetTop-100<=window.pageYOffset&&(n=e.getAttribute('id'))}),t.forEach(e=>{e.classList.remove('active'),e.getAttribute('href')==='#'+n&&e.classList.add('active')})});
